let rec digitsHelper n l =
  if n < 10 then n :: l else digitsHelper (n / 10) ((n mod 10) :: l);;

let digitsOfInt n = let l = [] in if n < 0 then l else digitsHelper n l;;

let rec digitalRoot n =
  let l = digitsOfInt n in
  let x = sumList l in if x = n then x else digitalRoot x;;
